import React, { useState } from "react";
import { Link } from "react-router-dom";
import "./Navbar.css";

const Navbar = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  // サインイン成功時の処理に合わせてisLoggedInを更新する関数
  const handleSignInSuccess = () => {
    // サインイン成功時の処理が完了した後、isLoggedInをtrueに設定する（例：実際の認証が成功した場合など）
    setIsLoggedIn(true);
  };

  return (
    <nav>
      <h2>Eiyo eats</h2>
        <Link to="/">
          Home
        </Link>
        {!isLoggedIn && <Link to="/signin">
          Signin
        </Link>}
        {isLoggedIn && <Link to="/search">
          Search
        </Link>}
    </nav>
  );
};

export default Navbar;
